    private void btFatorialActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btFatorialActionPerformed
        // TODO add your handling code here:
        Fatorial f = new Fatorial();
        int numero = Integer.parseInt(txNum.getValue().toString());
        f.setValor(numero);
        lbFormula.setText(f.getFormula());
        lbResultado.setText(Integer.toString(f.getFatorial()));
    }//GEN-LAST:event_btFatorialActionPerformed

